private Node tryFoldArrayAccess(Node n, Node left, Node right) {
    Node parent = n.getParent();

    // If GETPROP/GETELEM is used as assignment target the array literal is
    // acting as a temporary we can't fold it here:
    //    "[][0] += 1"
    if (isAssignmentTarget(n)) {
        return n;
    }

    if (!right.isNumber()) {
        // Sometimes people like to use complex expressions to index into
        // arrays, or strings to index into array methods.
        return n;
    }

    double index = right.getDouble();
    int intIndex = (int) index;
    if (intIndex != index) {
        error(INVALID_GETELEM_INDEX_ERROR, right);
        reportError(INVALID_GETELEM_INDEX_ERROR, right);
        return n;
    }

    if (intIndex < 0) {
        // Added edge case handling for negative indices
        if (left.isEmpty()) {
            error(INDEX_OUT_OF_BOUNDS_ERROR, right);
            reportError(INDEX_OUT_OF_BOUNDS_ERROR, right);
            return null; // Return null instead of trying to access empty array
        }
        error(INDEX_OUT_OF_BOUNDS_ERROR, right);
        reportError(INDEX_OUT_OF_BOUNDS_ERROR, right);
        return n;
    }

    Node current = left.getFirstChild();
    Node elem = null;
    for (int i = 0; current != null && i < intIndex; i++) {
        if (current == null) {
            // Added edge case handling for indices greater than or equal to array length
            error(INDEX_OUT_OF_BOUNDS_ERROR, right);
            reportError(INDEX_OUT_OF_BOUNDS_ERROR, right);
            return n;
        }
        elem = current;

        current = current.getNext();
    }

    if (elem == null) {
        error(INDEX_OUT_OF_BOUNDS_ERROR, right);
        reportError(INDEX_OUT_OF_BOUNDS_ERROR, right);
        return n;
    }

    if (elem.isEmpty()) {
        elem = NodeUtil.newUndefinedNode(elem);
    } else {
        left.removeChild(elem);
    }

    // Replace the entire GETELEM with the value
    n.getParent().replaceChild(n, elem);
    reportCodeChange();
    return elem;
}

