    try {
        mockInstance = classInstantiator.instantiate(mockedProxyType);

        // Check if the mock instance is of the correct type
        if (!mockedProxyType.isAssignableFrom(mockInstance.getClass())) {
            throw new ClassCastException("Mock instance is not of the correct type: " + describeClass(mockInstance) + " instead of " + describeClass(mockedProxyType));
        }

        MockMethodInterceptor.MockAccess mockAccess = (MockMethodInterceptor.MockAccess) mockInstance;
