public MultiKey(final K[] keys, final boolean makeClone) {
    super();
    if (keys == null) {
        throw new IllegalArgumentException("The array of keys must not be null");
    }
    if (makeClone) {
        this.keys = keys.clone();
    } else {
        this.keys = keys;
    }

    calculateHashCode(keys);
}
