    }
    if (this.legendItemURLGenerator instanceof PublicCloneable) {
        clone.legendItemURLGenerator = (CategorySeriesLabelGenerator)
                ObjectUtilities.clone(this.legendItemURLGenerator);
    }
    return clone;
