public boolean equals(Object obj) {
    if (! (obj instanceof FastDateParser) ) {
        return false;
    }
    FastDateParser other = (FastDateParser) obj;
    return pattern.equals(other.pattern)
        && timeZone.equals(other.timeZone)
        && locale.equals(other.locale);
}

