appendOptionGroup:
private static void appendOptionGroup(final StringBuffer buff, 
                                      final OptionGroup group)
{
    if (!group.isRequired())
    {
        buff.append("[");
    }

    List optList = new ArrayList(group.getOptions());
    Collections.sort(optList, new OptionComparator());
    // for each option in the OptionGroup
    for (Iterator i = optList.iterator(); i.hasNext();)
    {
        // whether the option is required or not is handled at group level
        appendOption(buff, (Option) i.next(), true);

        if (i.hasNext())
        {
            buff.append(" | ");
        }
    }

    if (!group.isRequired())
    {
        buff.append("]");
    }
}

appendOption:
private static void appendOption(final StringBuffer buff, 
                                 final Option option, 
                                 final boolean required)
{
    if (!required)
    {
        buff.append("[");
    }

    if (option.getOpt() != null)
    {
        buff.append("-").append(option.getOpt());
    }
    else
    {
        buff.append("--").append(option.getLongOpt());
    }

    // if the Option has a value
    if (option.hasArg() && (option.getArgName() != null))
    {
        buff.append(" <").append(option.getArgName()).append(">");
    }

    // if the Option is not a required option
    if (!required)
    {
        buff.append("]");
    }
}

renderWrappedText:
protected StringBuffer renderWrappedText(StringBuffer sb, int width, 
                                         int nextLineTabStop, String text)
{
    int pos = findWrapPos(text, width, 0);

    if (pos == -1)
    {
        sb.append(rtrim(text));

        return sb;
    }
    sb.append(rtrim(text.substring(0, pos))).append(defaultNewLine);

    // all following lines must be padded with nextLineTabStop space 
    // characters
    final String padding = createPadding(nextLineTabStop);

    while (true)
    {
        text = padding + text.substring(pos).trim();
        pos = findWrapPos(text, width, nextLineTabStop);

        if (pos == -1)
        {
            sb.append(text);

            return sb;
        }

        sb.append(rtrim(text.substring(0, pos))).append(defaultNewLine);
    }
}

findWrapPos:
protected int findWrapPos(String text, int width, int startPos)
{
    int pos = -1;

    // the line ends before the max wrap pos or a new line char found
    if (((pos = text.indexOf('\n', startPos)) != -1 && pos <= width)
        || ((pos = text.indexOf('\t', startPos)) != -1 && pos <= width))
    {
        return pos+1;
    }
    else if ((startPos + width) >= text.length())
    {
        return -1;
    }


    // look for the last whitespace character before startPos+width
    pos = startPos + width;

    char c;

    while ((pos >= startPos) && ((c = text.charAt(pos)) != ' ')
           && (c != '\n') && (c != '\r'))
    {
        --pos;
    }

    // if we found it - just return
    if (pos > startPos)
    {
        return pos;
    }

    // must look for the first whitespace chearacter after startPos 
    // + width
    pos = startPos + width;

    while ((pos <= text.length()) && ((c = text.charAt(pos)) != ' ')
           && (c != '\n') && (c != '\r'))
    {
        ++pos;
    }

    return (pos == text.length())        ? (-1) : pos;
}

createPadding:
protected String createPadding(int len)
{
    StringBuffer sb = new StringBuffer(len);

    for (int i = 0; i < len; ++i)
    {
        sb.append(' ');
    }

    return sb.toString();
}

rtrim:
protected String rtrim(String s)
{
    if ((s == null) || (s.length() == 0))
    {
        return s;
    }

    int pos = s.length();

    while ((pos > 0) && Character.isWhitespace(s.charAt(pos - 1)))
    {
        --pos;
    }

    return s.substring(0, pos);
}

compare:
public int compare(Object o1, Object o2)
{
    Option opt1 = (Option)o1;
    Option opt2 = (Option)o2;

    return opt1.getKey().compareToIgnoreCase(opt2.getKey());
}
