escape:
static void escape(StringBuilder accum, String string, Document.OutputSettings out,
                   boolean inAttribute, boolean normaliseWhite, boolean stripLeadingWhite) {

    boolean lastWasWhite = false;
    boolean reachedNonWhite = false;
    final EscapeMode escapeMode = out.escapeMode();
    final CharsetEncoder encoder = out.encoder();
    final CoreCharset coreCharset = CoreCharset.byName(encoder.charset().name());
    final Map<Character, String> map = escapeMode.getMap();
    final int length = string.length();

    int codePoint;
    for (int offset = 0; offset < length; offset += Character.charCount(codePoint)) {
        codePoint = string.codePointAt(offset);

        if (normaliseWhite) {
            if (StringUtil.isWhitespace(codePoint)) {
                if ((stripLeadingWhite && !reachedNonWhite) || lastWasWhite)
                    continue;
                accum.append(' ');
                lastWasWhite = true;
                continue;
            } else {
                lastWasWhite = false;
                reachedNonWhite = true;
            }
        }
        // surrogate pairs, split implementation for efficiency on single char common case (saves creating strings, char[]):
        if (codePoint < Character.MIN_SUPPLEMENTARY_CODE_POINT) {
            final char c = (char) codePoint;
            // html specific and required escapes:
            switch (c) {
                case '&':
                    accum.append("&amp;");
                    break;
                case 0xA0:
                    if (escapeMode != EscapeMode.xhtml)
                        accum.append("&nbsp;");
                    else
                        accum.append(c);
                    break;
                case '<':
                    if (!inAttribute)
                        accum.append("&lt;");
                    else
                        accum.append(c);
                    break;
                case '>':
                    if (!inAttribute)
                        accum.append("&gt;");
                    else
                        accum.append(c);
                    break;
                case '"':
                    if (inAttribute)
                        accum.append("&quot;");
                    else
                        accum.append(c);
                    break;
                default:
                    if (canEncode(coreCharset, c, encoder))
                        accum.append(c);
                    else if (map.containsKey(c))
                        accum.append('&').append(map.get(c)).append(';');
                    else
                        accum.append("&#x").append(Integer.toHexString(codePoint)).append(';');
            }
        } else {
            final String c = new String(Character.toChars(codePoint));
            if (encoder.canEncode(c)) // uses fallback encoder for simplicity
                accum.append(c);
            else
                accum.append("&#x").append(Integer.toHexString(codePoint)).append(';');
        }
    }
}

unescape:
static String unescape(String string, boolean strict) {
    return Parser.unescapeEntities(string, strict);
}

canEncode:
private static boolean canEncode(final CoreCharset charset, final char c, final CharsetEncoder fallback) {
    // todo add more charset tests if impacted by Android's bad perf in canEncode
    switch (charset) {
        case ascii:
            return c < 0x80;
        case utf:
            return true; // real is:!(Character.isLowSurrogate(c) || Character.isHighSurrogate(c)); - but already check above
        default:
            return fallback.canEncode(c);
    }
}

toCharacterKey:
private static Map<Character, String> toCharacterKey(Map<String, Character> inMap) {
    Map<Character, String> outMap = new HashMap<Character, String>();
    for (Map.Entry<String, Character> entry: inMap.entrySet()) {
        Character character = entry.getValue();
        String name = entry.getKey();

        if (outMap.containsKey(character)) {
            // dupe, prefer the lower case version
            if (name.toLowerCase().equals(name))
                outMap.put(character, name);
        } else {
            outMap.put(character, name);
        }
    }
    return outMap;
}
