private int getRelativePositionOfTextNode() {
    int count = 1;
    Node n = node.getPreviousSibling();
    while (n != null) {
        if (n.getNodeType() == Node.TEXT_NODE
            || n.getNodeType() == Node.CDATA_SECTION_NODE) {
