getAssignedValue:
static Node getAssignedValue(Node n) {
  Preconditions.checkState(isName(n));
  Node parent = n.getParent();
  if (isVar(parent)) {
    return n.getFirstChild();
  } else if (isAssign(parent) && parent.getFirstChild() == n) {
    return n.getNext();
  } else {
    return null;
  }
}

